for tav in [500, 1000, 2000]: open('sorbarendezett.txt', 'a', encoding='utf-8').writelines(f"{sor['teljesnev']}, {sor['tav']}, {sor['ido']//60}:{sor['ido'] % 60}\n" for sor in sorted([versenyzo for versenyzo in [{'teljesnev': (sor.strip().split(','))[0], 'tav': int((sor.strip().split(','))[1]), 'ido': int(((sor.strip().split(','))[2].split(':'))[0])*60 + int(((sor.strip().split(','))[2].split(':'))[1])} for sor in open('verseny.txt', 'r', encoding='utf-8')] if versenyzo['tav'] == tav], key=lambda elem: elem['ido']))